    return(

<div key={props.id} className="animal" >
             <div className='favAnim' onClick={()=>props.setFavList(...p)}> <FontAwesomeIcon icon={solid('heart')} size="2x"/>
                       
                       </div>
              <img src= {props.photo}  />
                <h2> {props.name}</h2>
                
                </div>
        
     


    )
}

export default AnimalCard
