import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { solid, regular, brands, icon } from '@fortawesome/fontawesome-svg-core/import.macro';




function AnimalCard(props) {
    function setFavorite(){
         let animal = props.favList.filter(x=>x.id===props.id);
         if(animal.length===0) props.setFavList([...props.favList,{id:props.id,photo:props.photo,animalName:props.name}])
    }
    return(

<div key={props.id} className="animal" >
             <div className='favAnim' onClick={setFavorite} Style={props.favList.filter(x=>x.id===props.id).length==0? {color:"#5d3ff3"}:{color:"#c0b5f7"}}> <FontAwesomeIcon icon={solid('heart')} size="2x"/>
                       
                       </div>
              <img src= {props.photo}  />
                <h2> {props.name}</h2>
                
                </div>
        
     


    )
}

export default AnimalCard
